<?phpsession_start();//  вся процедура работает на сесси.// Именно в ней хранятся данные  пользователя, пока он находится на сайте.// Очень важно запустить их в  самом начале странички!!!require "/models/users.php";//заносим введенный пользователем логин в переменную $login, если он пустой, то уничтожаем переменнуюif (isset($_POST['login'])) {    $login = $_POST['login'];    if ($login == '') {        unset($login);    }}//заносим введенный пользователем пароль в переменную $password, если он пустой, то уничтожаем переменнуюif (isset($_POST['password'])) {    $password=$_POST['password'];    if ($password =='') {        unset($password);    }}//если логин и пароль введены,то обрабатываем их, чтобы теги и скрипты не работали, мало ли что люди могут ввести$login = stripslashes($login);$login = htmlspecialchars($login);$password = stripslashes($password);$password = htmlspecialchars($password);//удаляем лишние пробелы$login = trim($login);$password = trim($password);$myrow = CheckUser($login);if (empty($myrow['password'])){    //если пользователя с введенным логином не существует    $regist="Извините, введённый вами login или пароль неверный.";    include ("/views/authentication.php");}else {    //если существует, то сверяем пароли    if ($myrow['password']==$password) {        //если пароли совпадают, то запускаем пользователю сессию! Можете его поздравить, он вошел!        $_SESSION['login']=$myrow['login'];        $_SESSION['id']=$myrow['id'];//эти данные очень часто используются, вот их и будет "носить с собой" вошедший пользователь        header("Location: /catalog.php");        //echo "Вы успешно вошли на сайт! <a href='eshop/'>Главная страница магазина</a>";    }    else {        //если пароли не сошлись        $regist="Извините, введённый вами login или пароль неверный.";        include ("/views/authentication.php");    }}?>