<?php//  вся процедура работает на сесси.// Именно в ней хранятся данные  пользователя, пока он находится на сайте.// Очень важно запустить их в  самом начале странички!!!require "/models/users.php";require "input_processing.php";switch ($action){    case "index":        $massageForUser="Вы вошли как гость";        include ("/views/authentication.php");    break;    case "authentication":        //заносим введенный пользователем логин в переменную $login, если он пустой, то уничтожаем переменную        if (isset($_POST['login'])) {            $currentUserLogin = $_POST['login'];            if ($currentUserLogin == '') {                unset($currentUserLogin);            }        }        //заносим введенный пользователем пароль в переменную $password, если он пустой, то уничтожаем переменную        if (isset($_POST['password'])) {            $currentUserPassword=$_POST['password'];            if ($currentUserPassword =='') {                unset($currentUserPassword);            }        }        if (empty($currentUserLogin) or empty($currentUserPassword))        {            $massageForUser="Вы вошли как гость";            include ("/views/authentication.php");            exit();        }        $currentUserLogin = clearInput($currentUserLogin);        $currentUserPassword = clearInput($currentUserPassword);        $resultOfChecking = checkingIfUserExists($currentUserLogin);        if (empty($resultOfChecking['password']))        {            //если пользователя с введенным логином не существует            $massageForUser="Извините, введённый вами login или пароль неверный.";            include ("/views/authentication.php");        }        else {            //если существует, то сверяем пароли            if ($resultOfChecking['password']==$currentUserPassword) {                //если пароли совпадают, то запускаем пользователю сессию! Можете его поздравить, он вошел!                $_SESSION['login']=$resultOfChecking['login'];                $_SESSION['id']=$resultOfChecking['id'];//эти данные очень часто используются, вот их и будет "носить с собой" вошедший пользователь                header("Location: /catalog/index");                //echo "Вы успешно вошли на сайт! <a href='eshop/'>Главная страница магазина</a>";            }            else {                //если пароли не сошлись                $massageForUser="Извините, введённый вами login или пароль неверный.";                include ("/views/authentication.php");            }        }    break;    default:        $massageForUser="Вы вошли как гость";        include ("/views/authentication.php");    break;}