<?phprequire "/models/users.php";//заносим введенный пользователем логин в переменную $login, если он пустой, то уничтожаем переменнуюif (isset($_POST['login'])) {    $login = $_POST['login'];    if ($login == '') {        unset($login);    }}//заносим введенный пользователем пароль в переменную $password, если он пустой, то уничтожаем переменнуюif (isset($_POST['password'])) {    $password=$_POST['password'];    if ($password =='') {        unset($password);    }}//если пользователь не ввел логин или пароль, то выдаем ошибку и останавливаем скриптif (empty($login) or empty($password)){    $regist="Заполните пожалуйста все поля";    include ("/views/registration.php");    exit();}//если логин и пароль введены, то обрабатываем их, чтобы теги и скрипты не работали, мало ли что люди могут ввести$login = stripslashes($login);$login = htmlspecialchars($login);$password = stripslashes($password);$password = htmlspecialchars($password);//удаляем лишние пробелы$login = trim($login);$password = trim($password);$myrow = CheckUser($login);if (!empty($myrow['id'])) {    exit ("Извините, введённый вами логин уже зарегистрирован. Введите другой логин.");}// Проверяем, есть ли ошибки$result2 = IfRegistrated($login,$password);if ($result2=='TRUE'){    echo "Вы успешно зарегистрированы! Теперь вы можете зайти на сайт. <a href='../index.php'>Главная страница</a>";}else {    echo "Ошибка! Вы не зарегистрированы.";}?>